package transaction

import (
	"testing"
)

const txRedeemCheck = ""

func TestTransactionRedeemCheck_Sign(t *testing.T) {
	t.Parallel()
	data := NewRedeemCheckData().
		MustSetProof("da021d4f84728e0d3d312a18ec84c21768e0caa12a53cb0a1452771f72b0d1a91770ae139fd6c23bcf8cec50f5f2e733eabb8482cf29ee540e56c6639aac469600").
		MustSetRawCheck("")

	tx, err := NewBuilder(TestNetChainID).NewTransaction(data)
	if err != nil {
		t.Fatal(err)
	}

	transaction := tx.SetNonce(1).SetGasPrice(1).SetGasCoin(1)

	signedTx, err := transaction.Sign("05ddcd4e6f7d248ed1388f0091fe345bf9bf4fc2390384e26005e7675c98b3c1")
	if err != nil {
		t.Fatal(err)
	}

	encode, err := signedTx.Encode()
	if err != nil {
		t.Fatal(err)
	}
	if encode != txRedeemCheck {
		t.Errorf("EncodeTx got %s, want %s", string(encode), txRedeemCheck)
	}
}

func TestDecode_redeemCheck(t *testing.T) {
	t.Parallel()
	decode, err := Decode(txRedeemCheck)
	if err != nil {
		t.Fatal(err)
	}

	if decode.Fee().String() != "30000000000000000" {
		t.Error("redeem check transaction fee is invalid", decode.Fee().String())
	}
}
